#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="$basedir\E:\EnderXiao.github.io\node_modules\.store\hexo@7.1.1\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="$basedir/E:/EnderXiao.github.io/node_modules/.store/hexo@7.1.1/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$env_node_path$pathsep$new_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../.store/hexo@7.1.1/node_modules/hexo/bin/hexo" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../.store/hexo@7.1.1/node_modules/hexo/bin/hexo" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../.store/hexo@7.1.1/node_modules/hexo/bin/hexo" $args
  } else {
    & "node$exe"  "$basedir/../.store/hexo@7.1.1/node_modules/hexo/bin/hexo" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
